function NERDTag($http){
	this.addPersonalTag = function(number,tmpId, kbId, tag, sessionId){
		return new Promise(function(resolve, reject){
			$http({
				 url: '/api/x_26385_crs_kd/export/addPersonalTag',
				 method : "POST",
				 beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', sessionId); },
				 headers: {'Content-Type': 'application/json'},
				 data: {number: number, tmpId: tmpId, kbId: kbId, tag: tag} 				
			}).then(resolve, reject);
		});
	};
