sp_header_footer.client_script:
function($scope, $rootScope, spGtd) {
	/* widget controller */
	var c = this;

	$scope.logout = function(){
		window.location.href="/logout.do";
	};

	c.openUserGuide = function(){
		//var url = '/sys_attachment.do?view=true&sys_id='+ c.data.helpID;
		var url = '/api/x_26385_crs_kd/export/getUserGuide';
		window.open(url, '_blank');
	};

	$rootScope.user = {};
	$rootScope.user.name = c.data.userName;
	$rootScope.user.id = c.data.userId;
	$rootScope.user.role = c.data.role;
	$rootScope.user.doc = c.data.doc;



	c.tour = function(){
		spGtd.launch('ee07f9a4dba10c50b86770c08c9619b0');
		//alert('test');

	}

	}

sp_header_footer.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	//data.sessionId = gs.getSession().getSessionToken();
	data.userName = gs.getUserDisplayName();
	var user_sys_id = gs.getUserID();
	data.userId = user_sys_id;
	data.role = [];
	data.doc = '';
	var nerd_user = new GlideRecord('x_26385_crs_kd_nerd_user');
	nerd_user.addQuery("user",user_sys_id);
	nerd_user.query();
	if(nerd_user.getRowCount() == 0){
		data.role.push('sm');
		data.role_name = 'NCI Staff Member';
	}
	else{
		nerd_user.next();
		data.doc = nerd_user.doc.toString();
		var type = nerd_user.user_type.toString();
		if(type == 'Super User'){
			data.role.push('su');
			data.role_name = 'Super User';
		}
		else if(type == 'CRS User'){
			data.role.push('cu');
			data.role_name = 'CRS User';
		}
		else if(type == 'Regular User'){
			data.role.push('ru');
			data.role_name = 'Regular User';
		}
		else if(type == 'DOC Planning Contact'){
			data.role.push('dpc');
			data.role_name = 'DOC Planning Contact';
		}
		else if(type == 'CRS Reviewer'){
			data.role.push('cr');
			data.role_name = 'CRS Reviewer';
		}
	}

	var document_id = "b149e3c0dbaabf00b86770c08c9619a1";
    var gr = new GlideRecord('sys_attachment');
	gr.addQuery("table_sys_id", document_id);
	gr.addQuery('content_type','STARTSWITH','application/pdf');
	gr.query();
	gr.next();
	data.helpID = gr.sys_id.toString();

})();
