sp_widget.client_script:
function($scope,spModal, $rootScope, $location) {
  var c = this;
  var s = $location.search();
  var redirect = false;

	c.isSu = $rootScope.user.role.indexOf('su') > -1;
	c.isCu = $rootScope.user.role.indexOf('cu') > -1;
	c.isRu = $rootScope.user.role.indexOf('ru') > -1;
	c.isCr = $rootScope.user.role.indexOf('cr') > -1;
	c.isDpc = $rootScope.user.role.indexOf('dpc') > -1;
	c.isSm = $rootScope.user.role.indexOf('sm') > -1;
	c.currentUserID = $rootScope.user.id;

	if($rootScope.user && (c.isSm || c.isDpc)){
		if(s.xpage && s.xpage == 'kd_new_submission'){
			c.xpage = "new_submission";
		}
		else if(s.xpage && s.xpage == 'kd_submission_edit'){
			c.xpage = "submission_edit";
			c.number = s.number;
		}
		else{
			c.xpage = "submissions";
		}
	}
	else if (s.xpage && s.xpage == 'kd_search') {
			c.xpage = "main";
		if(s.kb_knowledge_base && c.data.kbs[s.kb_knowledge_base]){
			c.kb_knowledge_base_name = c.data.kbs[s.kb_knowledge_base];
			c.kb_knowledge_base = s.kb_knowledge_base;
		}
		else{
			c.kb_knowledge_base_name = "NERD";
			c.kb_knowledge_base = 'f5644dcadb54ab0054d8ff621f9619d7';
		}
	}
	else if(s.xpage && s.xpage == 'kd_new_submission'){
			c.xpage = "new_submission";
		}
	else if(s.xpage && s.xpage == 'kd_submissions'){
			c.xpage = "submissions";
	}
	else if(s.xpage && s.xpage == 'kd_submission_edit'){
			c.xpage = "submission_edit";
			c.number = s.number;
	}
	else if(s.xpage && s.xpage == 'kd_new_entry'){
		c.xpage = "new_entry";
	}
	else if(s.xpage && s.xpage == 'kd_settings'){
		c.xpage = "settings";
	}
	else if(s.xpage && s.xpage == 'kd_faqs'){
		c.xpage = "faqs";
	}
	else if(s.xpage && s.xpage == 'kd_glossary'){
		c.xpage = "glossary";
	}
	else if(s.xpage && s.xpage == 'kd_article_view'){
		c.xpage = "article_view";
		c.number = s.number;
		if(s.kb_knowledge_base && c.data.kbs[s.kb_knowledge_base]){
			c.kb_knowledge_base_name = c.data.kbs[s.kb_knowledge_base];
			c.kb_knowledge_base = s.kb_knowledge_base;
		}
		else{
			c.kb_knowledge_base_name = "NERD";
			c.kb_knowledge_base = 'f5644dcadb54ab0054d8ff621f9619d7';
		}
	}
	else if(s.xpage && s.xpage == 'kd_article_edit'){
		c.xpage = "article_edit";
		c.number = s.number;
		if(s.kb_knowledge_base && c.data.kbs[s.kb_knowledge_base]){
			c.kb_knowledge_base_name = c.data.kbs[s.kb_knowledge_base];
			c.kb_knowledge_base = s.kb_knowledge_base;
		}
		else{
			c.kb_knowledge_base_name = "NERD";
			c.kb_knowledge_base = 'f5644dcadb54ab0054d8ff621f9619d7';
		}
	}
	else{
		c.xpage = "home";
	}



	//Search on enter key pressed
	c.inputOnkeypress = function(){
		if (event.keyCode === 13) {
			c.searchKB();
		}
	}

	c.showAllContent = function(){
		c.xpage = "main";
		s.xpage = 'kd_search'; // this is our default page
    $location.search(s).replace();
	}

	c.searchKB = function(){
		c.xpage = "main";
		s.xpage = 'kd_search';
		s.search = c.data.searchString;
    $location.search(s).replace();
	}

	window.showMessageBox = function(message, willgoto){
		spModal.open({
			 title: 'Information Dialog',
			 message: '<div class="spModal_content">'+message+'</div>',
			 backdrop: 'static',
       keyboard: false,
			 buttons: [
                {label:'Ok', primary: true}
            ]
			}).then(function() {
					if(willgoto){
						//s.xpage = willgoto.xpage;
						for(var key in willgoto){
							s[key] = willgoto[key];
						}
						$location.search(s).replace();
					}
        }, function() {

        });
	}

}

sp_widget.script:
(function() {  /* populate the 'data' object */  /* e.g., data.table = $sp.getValue('table'); */
	data.kbs = {
		'f5644dcadb54ab0054d8ff621f9619d7': "NERD",
		'ff315a9edba47340b86770c08c961999': "ROCK",
		'2c42d2dedba47340b86770c08c9619a9': "Moonshot Evaluation"
	};

})();
