sp_widget.client_script:
function($scope,$sce, $location, NERDSearchBasicArticleByID, NERDComment, NERDTag, NERDFile) {
  /* widget controller */
  var c = this;
	var s = $location.search();
	var template = {"u_kb_template_top_accomplishment":
									['Cancer Site/Type',
									 'Research Type',
									 'Special Topic',
									 'Description',
									 'References',
									 'Involved organization', 
									 'DOC', 
									 'Fiscal Year'],
									"u_kb_template_gpra": 
									[
									 'Measure ID',
									 'Research Type',
									 'Special Topic',
									 'DOC',
									 'Contacts',
									 'Description',
									 'Summary',
									 'Background',
									 'Target Context and Conditions', 
									 'Fiscal Year'],
									"u_kb_template_miscellaneous": 
									[
									 'Cancer Site/Type',
