sp_widget.client_script:
function($scope, $location,$mdDialog, NERDArticle) {
  /* widget controller */
  var c = this;
	var s = $location.search();

	c.saving = false;
	c.save_text = "Save";
	c.save_version_text = "Save this Version";
	c.data.isMainVersion = false;

	c.attachments = [{name: 'file2upload'}];
	c.prev_attachments = [];

	if(s.tid){
		c.data.selectedKnowledgeCategory = s.tid;
	}


	//fontselect
	c.mceOptions = {
		resize: true,
		content_css : '/133734fbdb4ab30054d8ff621f9619f2.spcssdbx',
		height: 200,
		menubar: false,
		statusbar: false,
		plugins: 'link autoresize table',
		default_link_target: "_blank",
		toolbar: 'undo redo | fontsizeselect bold italic | alignleft aligncenter alignright alignjustify | bullist numlist | link | table',
		table_default_styles: {
			'width': '50%',
			'border-collapse': 'collapse',
			'table-layout': 'fixed'
		}
  }

	c.reorderItems = function(items){
		var result = [];
		var len = items.length;
		var rows = Math.floor(len/3);
		if(len % 3 != 0){
			rows ++;
		}
		var k = 0, i = 0;
		for(k = 0; k< rows ; k++){
			for(i = k; i< rows * 3; i = i + rows){
				if(i >= len){
					result.push('');
				}
				else{
					result.push(items[i]);
				}

			}
		}
		return result;
	};

	c.transform = function(field, arr_1, arr_2){
		if(arr_1 == undefined && arr_2 == undefined){
			return;
		}
		var sub = [];
		if(c.data[field]){
			arr_1.forEach(function(idx){
				c.schema[idx].display = true;
				if(c.schema[idx].type == 'boolean' && c.schema[idx].displayWhenTrue !== undefined){
					sub.push(idx);
				}
			});
			arr_2.forEach(function(idx){
				c.schema[idx].display = false;
				if(c.schema[idx].type == 'boolean' && c.schema[idx].displayWhenTrue !== undefined){
					sub.push(idx);
				}
			});
		}
		else{
			arr_1.forEach(function(idx){
				c.schema[idx].display = false;
				if(c.schema[idx].type == 'boolean' && c.schema[idx].displayWhenTrue !== undefined){
					sub.push(idx);
				}
			});
			arr_2.forEach(function(idx){
				c.schema[idx].display = true;
				if(c.schema[idx].type == 'boolean' && c.schema[idx].displayWhenTrue !== undefined){
					sub.push(idx);
				}
			});
		}
		if(sub.length > 0){
			sub.forEach(function(s){
				if(!c.schema[s].display){
					c.data[c.schema[s].field] = false;
					if(c.schema[s].field == 'x_26385_crs_kd_u_kb_includes_analysis'){
						c.transform(c.schema[s].field, c.schema[s].displayWhenTrue, c.schema[s].displayWhenFalse);
					}
				}
			});
		}

	};

	var ps = {};
	ps.template = s.tid;
	ps.number = s.number;
	ps.version = s.version ? s.version : "";
	NERDArticle.getArticleWithSchema(ps, c.data.sessionId).then(function(res){
		var result = res.data.result;
		c.schema = result.schema;
		c.data = result.content;
		c.items = {};
		c.selected = {};
		c.version = result.version;
		if(c.version.indexOf(".") == -1){
			c.version += ".0";
		}
		c.schema.forEach(function(item){
			if(item.type == "checkbox"){
				c.items[item.field] = item.items;
				item.items = c.reorderItems(item.items);
				c.selected[item.field] = c.data[item.field] == "" ? [] : c.data[item.field].split(',');
			}
			else if(item.type == "boolean"){
				c.data[item.field] = c.data[item.field] == "Yes";
				if(item.displayWhenTrue){
					c.transform(item.field, item.displayWhenTrue, item.displayWhenFalse);
				}
			}
			else if(item.type == "date"){
				if(c.data[item.field] != ""){
					c.data[item.field] = c.data[item.field] + 'T05:00:00.000Z';
					c.data[item.field] = new Date(c.data[item.field]);
				}
			}
			else if(item.type == "customize"){
				if(item.label == 'References'){
					c.data['pmids'] = c.data[item.field];
				}
				if(item.label == 'Funding'){
					var arr = c.data[item.field].split('|');
					c.data['Funding'] = (arr[0] == "Yes");
					c.data['Funding_info'] = arr[1];
				}
			}
		});
		c.prev_attachments = result.attachments;
		$scope.$apply();
	},function(error){
		console.log(error);
	});

	c.addAttachment = function(event){
		var el = event.currentTarget;
		//c.attachments.pop();
    var node = el.parentNode;
		var content = angular.element("<div style=\"margin-bottom:10px;\">"
			+"<input type=\"file\" style=\"width:80%;float:left;cursor: pointer;\" name=\"file2upload\" class=\"form-control\">"
			+"<span class=\"attachment-action\" ng-click=\"c.addAttachment($event)\"><i class=\"fa fa-plus\"></i></span>"
			+"<span class=\"attachment-action\" ng-click=\"c.deleteAttachment($event)\" ng-show = \"c.attachments.length > 1\"><i class=\"fa fa-minus\"></i></span>"
			+"</div>");
		content.insertAfter(node);
		c.attachments.push({name: 'file2upload'});
		$compile(content)($scope);
	};

	c.deleteAttachment = function(event){
		var el = event.currentTarget;
		c.attachments.pop();
    el.parentNode.parentNode.removeChild(el.parentNode);
	};

	c.removeAttachment = function(attach_sys_id){
		var idx = 0;
		var count = 0;
		c.prev_attachments.forEach(function(att){
			if(att.attach_sys_id == attach_sys_id){
				idx = count;
			}
			count ++;
		});
		c.prev_attachments.splice(idx, 1);
	};

	c.toggle = function (item, list) {
    var idx = list.indexOf(item);
    if (idx > -1) {
      list.splice(idx, 1);
    }
    else {
      list.push(item);
    }
  };

	c.exists = function (item, list) {
		return list.indexOf(item) > -1;
  };

	c.isIndeterminate = function(field) {
    return (c.selected[field].length !== 0 &&
        c.selected[field].length !== c.items[field].length);
  };

	c.isChecked = function(field) {
		return c.selected[field].length === c.items[field].length;
  };

  c.toggleAll = function(field) {
    if (c.selected[field].length === c.items[field].length) {
      c.selected[field] = [];
    } else if (c.selected[field].length === 0 || c.selected[field].length > 0) {
      c.selected[field] = c.items[field].slice(0);
    }
  };

	c.tosave = function(ev){
		var input = $('input[name=file2upload]');
		var length = input.length;
		var pass = true;
		var errorMessage = "";
		var element;
		for(var k = 0; k< length; k++){
			element = input[k];
			if(element.files.length == 0){
				continue;
			}
			var file = element.files[0];
			if(file.type !== 'application/pdf' && file.type !== 'application/vnd.ms-powerpoint' 
				 && file.type !== 'application/vnd.openxmlformats-officedocument.presentationml.presentation'
				 && file.type !== 'application/msword'
				 && file.type !== 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'){
				pass = pass && false;
				errorMessage = 'Please attach a PDF/PPT/Word file.';
			}
			else if(file.size > 18874368){
				pass = pass && false;
				errorMessage = 'Please make sure file size is less than 18MB.';
			}
		};

		if(!c.data.x_26385_crs_kd_u_kb_title || c.data.x_26385_crs_kd_u_kb_title.trim() == ""){
			window.showMessageBox("Please enter a title for you submission.");
		}
		else if(!pass){
			window.showMessageBox(errorMessage);
		}
		else{
		  $mdDialog.show({
				contentElement: '#myDialog',
				parent: angular.element(document.body),
				targetEvent: ev,
				clickOutsideToClose: false
			});
		}

	};

	function pReadFile(file){
		return new Promise(function(resolve, reject){
			var fr = new FileReader();  
			fr.onload = function(){
				var encoded = fr.result.replace(/^data:(.*;base64,)?/, '');
				if ((encoded.length % 4) > 0) {
					encoded += '='.repeat(4 - (encoded.length % 4));
				}
				resolve(encoded);
			};
			fr.readAsDataURL(file);
		});
	}

	function readFile(file, callback) {
		var fileReader = new FileReader();
		fileReader.onload = function() {
			//var result = fileReader.result.replace('data:application/pdf;base64,', '');
			var encoded = fileReader.result.replace(/^data:(.*;base64,)?/, '');
      if ((encoded.length % 4) > 0) {
        encoded += '='.repeat(4 - (encoded.length % 4));
      }
			callback(encoded);
		}
		fileReader.readAsDataURL(file); //file must be base64
	}

	c.saveAttachment = function(version, input,template, sys_id, sessionId, idx){
		if(input.length <= idx){
			return new Promise(function(resolve, reject){
								resolve(version);
							});
		}
		else if(input[idx].files.length == 0){
			return c.saveAttachment(version, input,template, sys_id, sessionId, idx + 1);
		}
		else{
			var file = input[idx].files[0];
			return new Promise(function(resolve, reject){
				pReadFile(file).then(function(base64){
					var attach = {
						template: template,
						version: version,
						number: ps.number,
						sys_id: sys_id,
						fileName: file.name,
						fileType: file.type,
						fileContents : base64
					};
					NERDArticle.saveAttachmentWithVersion(attach, sessionId).then(function(res){
						var result = res.data.result;
						c.saveAttachment(version, input, template, sys_id, sessionId, idx + 1).then(function(dt){
							resolve(dt);
						},
						function(error){
							console.log(error);
						});

					},function(error){
						console.log(error);
					});
				},function(error){
					console.log(error);
				});
			});


		}
	};

	c.saveVersion = function(){
		var input = $('input[name=file2upload]');
		var params = {};
		c.schema.forEach(function(item){
			if(item.display !== undefined && !item.display){
				params[item.field] = item.type == 'boolean' ? "No" : "";
			}
			else if(item.type == 'boolean'){
				params[item.field] = (c.data[item.field] == undefined || c.data[item.field] == "" || !c.data[item.field]) ? "No" : "Yes";
			}
			else if(item.type == 'date'){
				if(c.data[item.field] == undefined){
					params[item.field] = "";
				}
				else if(c.data[item.field] !== ""){
					params[item.field] = new Date(c.data[item.field]).toISOString().substring(0, 10);
				}
			}
			else if(item.type == 'checkbox'){
				if(c.selected[item.field].length > 0){
					params[item.field] = '{'+c.selected[item.field].join("},{") + '}';
				}
				else{
					params[item.field] = "";
				}
			}
			else if(item.type == 'string' || item.type == 'html'){
				params[item.field] = c.data[item.field];
			}
			else if(item.type == 'radio'){
				params[item.field] = c.data[item.field];
			}
			else if(item.type == 'customize'){
				if(item.label == 'References'){
					var pmids = c.data['pmids'];
					var links = [];
					if(pmids && pmids.trim() !== ""){
						var p = pmids.trim().split(',');
						p.forEach(function(t){
							if(t !== ""){
								links.push(t);
							}
						});
					}
					params[item.field] = links.join(',');
				}
				else if(item.label == 'Funding'){
					var funding = [];
					funding.push((c.data['Funding'] != undefined && c.data['Funding']) ? "Yes":"No");
					funding.push(c.data['Funding_info']);
					params[item.field] = funding.join('|');
				}

			}
			else if(item.type == 'file'){

			}
		});

		params.number = ps.number;
		params.version = c.version;
		params.isMainVersion = c.data.isMainVersion;
		//params.specifyVersion = c.data.specifyVersion;
		params.annotation = c.data.annotation;
		var att_arr = [];
		c.prev_attachments.forEach(function(att){
			att_arr.push(att.attach_sys_id);
		});
		params.attachments = att_arr.length > 0 ? att_arr.join(",") : "";


		var template = s.tid;

		c.saving = true;
		c.save_text = "Saving...";

		//console.log(params);

		NERDArticle.saveVersion(template, params, c.data.sessionId).then(function(res){
			var result = res.data.result;
			if(result == "0"){
				return new Promise(function(resolve, reject){
					resolve("0");
				});
			}
			else{
				var version = result.version;
				var sys_id = result.sys_id;
				return c.saveAttachment(version, input, template, sys_id, c.data.sessionId, 0);
			}
		},function(error){
			console.log(error);
		}).then(function(rp){
			c.saving = false;
			c.save_text = "Save";
			var v = rp;
			window.showMessageBox("Article has been successfully saved.",{xpage:'kd_article_view', version: v});

		}, function(error){
			console.log(error);
		});
	}

	c.cancelDialog = function(){
		$mdDialog.cancel();
	};

	c.goBacktoArticle = function(){
		s.xpage = "kd_article_view";
		$location.search(s).replace();
	}

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.sessionId = gs.getSession().getSessionToken();
})();
