	var htmlToWord = (function() {
      var uri = 'data:text/html,';
      var template = '<html xmlns:office="urn:schemas-microsoft-com:office:office" xmlns:word="urn:schemas-microsoft-com:office:word" xmlns="http://www.w3.org/TR/REC-html40"><head><xml><word:WordDocument><word:View>Print</word:View><word:Zoom>90</word:Zoom><word:DoNotOptimizeForBrowser/></word:WordDocument></xml></head><body>{content}</body></html>';
      return function(data) {
				 var a = document.createElement('a');
					document.body.appendChild(a);
					a.setAttribute('style', 'display:none');
					a.setAttribute('href', uri + encodeURIComponent((template.replace("{content}",data))));
					var today = new Date();
					var dd = today.getDate();
					var mm = today.getMonth()+1;
					var yyyy = today.getFullYear();

					if(dd<10) {
							dd = '0'+dd
					} 

					if(mm<10) {
							mm = '0'+mm
					} 

					today = yyyy + "-"+ mm + "-" + dd;	
					a.setAttribute('download', "NERD_report_"+today+".doc");
					a.click();
          document.body.removeChild(a);
      }
    })()

  c.export2Excel = function () {
		var req = {
		 method: 'POST',
		 url: '/api/x_26385_crs_kd/export/excel',
		 headers: {
			 'Content-Type': 'application/json'
		 }
		}

		req.data = {};
		req.data.files = [];
		document.getElementsByName('selections').forEach(function(obj){
			if(obj.checked){
				var fs = {};
				var info = obj.value.split(',');
				fs.sys_id = info[0];
				//fs.kb = info[1];
				fs.category = info[2];
				req.data.files.push(fs);
			}
		});

		$http(req).then(function(resp){
			var content = resp.data.result.content;
			var data = [];
			var div = document.createElement("div");
			content.forEach(function(ct){
					//var tmp = {};
					//tmp["Date entered"] = ct.published;
					//tmp.Database = ct.database;
					//tmp.Category = ct.category;
					//tmp.Title = ct.title;
					//tmp["Research Type"] = ct.x_26385_crs_kd_u_kb_research_type;
					//tmp["Fiscal Year"] = ct.x_26385_crs_kd_u_kb_fiscal_year;
				if(ct["Description"] !== ""){
					div.innerHTML = ct["Description"];
					ct["Description"] = div.textContent || div.innerText || "";
				}

				if(ct["References"] !== ""){
					div.innerHTML = ct["References"];
					ct["References"] = div.textContent || div.innerText || "";
				}

				if(ct["Involved organization"] !== ""){
					div.innerHTML = ct["Involved organization"];
					ct["Involved organization"] = div.textContent || div.innerText || "";
				}

				if(ct["Summary"] !== ""){
					div.innerHTML = ct["Summary"];
					ct["Summary"] = div.textContent || div.innerText || "";
				}

				if(ct["Background"] !== ""){
					div.innerHTML = ct["Background"];
					ct["Background"] = div.textContent || div.innerText || "";
				}

				if(ct["Target Context and Conditions"] !== ""){
					div.innerHTML = ct["Target Context and Conditions"];
					ct["Target Context and Conditions"] = div.textContent || div.innerText || "";
				}
				data.push(ct);
			});

			/* generate a worksheet */
			var ws = XLSX.utils.json_to_sheet(data);

			/* add to workbook */
			var wb = XLSX.utils.book_new();
			XLSX.utils.book_append_sheet(wb, ws, "sheet1");

			/* write workbook and force a download */
			var today = new Date();
			var dd = today.getDate();
			var mm = today.getMonth()+1;
			var yyyy = today.getFullYear();

			if(dd<10) {
					dd = '0'+dd
			} 

			if(mm<10) {
					mm = '0'+mm
			} 

			today = yyyy + "-"+ mm + "-" + dd;
			XLSX.writeFile(wb, "NERD_report_"+today+".xlsx");
		}, function(error_resp){
			console.log(error_resp);
		});
  }

	c.export2Word = function () {
		var req = {
		 method: 'POST',
		 url: '/api/x_26385_crs_kd/export/word',
		 headers: {
			 'Content-Type': 'application/json'
		 }
		}

		req.data = {};
		req.data.files = [];
		document.getElementsByName('selections').forEach(function(obj){
			if(obj.checked){
				var fs = {};
				var info = obj.value.split(',');
				fs.sys_id = info[0];
				//fs.kb = info[1];
				fs.category = info[2];
				req.data.files.push(fs);
			}
		});

		$http(req).then(function(resp){
			var content = resp.data.result.content;
			var dt = '';
			content.forEach(function(ct){
				console.log(ct);
					dt += '<h1 style="text-align:center;">'+ct.title+'</h1>';
					dt += '<h3>Category:</h3>';
					dt += '<p>'+ct.category+'</p>';
				  for(var key in ct){
						if(key != 'title'){
							dt += '<h3>'+key+':</h3>';
							dt += '<p>'+ct[key]+'</p>';
						}
					}
			});
			htmlToWord(dt);
		}, function(error_resp){
			console.log(error_resp);
		});

  }

	c.data.doc = [];
	var cache = c.data.tmp.doc;
	cache.sort();
	cache.forEach(function(elem){
		c.data.doc.push({id: elem, name: elem.substring(1, elem.length -1), count: c.data.count.doc[elem]});
	});

	c.data.cancerSite = [];
	cache = c.data.tmp.cancerSite;
	cache.sort();
	cache.forEach(function(elem){
		c.data.cancerSite.push({id: elem, name: elem.substring(1, elem.length -1), count: c.data.count.cancerSite[elem]});
	});
	c.data.researchType = [];
	cache = c.data.tmp.researchType;
	cache.sort();
	cache.forEach(function(elem){
		c.data.researchType.push({id: elem, name: elem.substring(1, elem.length -1), count: c.data.count.researchType[elem]});
	});
	c.data.specialTopic = [];
	cache = c.data.tmp.specialTopic;
	cache.sort();
	cache.forEach(function(elem){
		c.data.specialTopic.push({id: elem, name: elem.substring(1, elem.length -1), count: c.data.count.specialTopic[elem]});
	});
	c.data.fiscalYear = [];
	cache = c.data.tmp.fiscalYear;
	cache.sort();
	cache.forEach(function(elem){
		c.data.fiscalYear.push({id: elem, name: elem.substring(1, elem.length -1), count: c.data.count.fiscalYear[elem]});
	});

	if(s.search){
		c.data.searchString = s.search;
		c.search();
	}
	else if(s.hasFilter && s.hasFilter == '1'){
		c.data.searchString = $rootScope.searchString;
		c.data.selectedYear = $rootScope.selectedYear?$rootScope.selectedYear : [];
		c.data.selectedDoc = $rootScope.selectedDoc?$rootScope.selectedDoc : [];
		c.data.selectedCancerSite = $rootScope.selectedCancerSite?$rootScope.selectedCancerSite : [];
		c.data.selectedResearchType = $rootScope.selectedResearchType?$rootScope.selectedResearchType : [];
		c.data.selectedSpecialTopic = $rootScope.selectedSpecialTopic?$rootScope.selectedSpecialTopic : [];
		c.search();
	}
	document.getElementById("progressing-bar").style.display = "none";
}

sp_widget.script:
(function($location) {
	data.sessionId = gs.getSession().getSessionToken();
	var infos_category = {'Top Accomplishments': {template_table: 'u_kb_template_top_accomplishment', filters:{"cancerSite":"x_26385_crs_kd_u_kb_cancer_site_type",
																																																						 "researchType":"x_26385_crs_kd_u_kb_research_type",
																																																						 "doc":"x_26385_crs_kd_u_kb_doc",
																																																						 "specialTopic":"x_26385_crs_kd_u_kb_special_topic",
																																																						 "fiscalYear":"x_26385_crs_kd_u_kb_fiscal_year"}},
												'GPRA': {template_table: 'u_kb_template_gpra', filters:{"doc":"x_26385_crs_kd_u_kb_doc",
																																								"researchType":"x_26385_crs_kd_u_kb_research_type",
																																								"specialTopic":"x_26385_crs_kd_u_kb_special_topic",
																																								"fiscalYear":"x_26385_crs_kd_u_kb_fiscal_year"}},
												'Miscellaneous': {template_table: 'u_kb_template_miscellaneous', filters:{"cancerSite":"x_26385_crs_kd_u_kb_cancer_site_type",
																																								"researchType":"x_26385_crs_kd_u_kb_research_type",
																																								"specialTopic":"x_26385_crs_kd_u_kb_special_topic",
																																								"fiscalYear":"x_26385_crs_kd_u_kb_fiscal_year"}}
							};
	data.knowledgeBases = [];
	var gr = new GlideRecord('kb_knowledge_base');
	gr.addQuery("title", "Basic Knowledge Base");
	gr.query();
	gr.next();
	var kb_id = gr.sys_id.toString();
	data.knowledgeBases.push({id: kb_id, name: gr.title.toString()});
	data.selectedKnowledgeBase = {id: kb_id, name: gr.title.toString()};

	var categories = [];
	/*
	gr = new GlideRecord('kb_category');
	gr.addQuery('parent_id', kb_id);
	gr.orderBy('sys_created_on');
	gr.query();
	var categories = [];
	while (gr.next()) {
		var obj = {};
		categories.push({id: gr.sys_id.toString(), name: gr.label.toString()});
	}
	*/

	categories.push({name: 'Top Accomplishments'});
	categories.push({name: 'GPRA'});
	categories.push({name: 'Miscellaneous'});

	data.count = {};
	cache = {};
	data.tmp = {};
	data.count.doc = {};
	data.tmp.doc = [];
	cache.doc = [];
	data.tmp.cancerSite = [];
	data.count.cancerSite = {};
	cache.cancerSite = [];
	data.tmp.researchType = [];
	data.count.researchType = {};
	cache.researchType = [];
	data.tmp.specialTopic = [];
	data.count.specialTopic = {};
	cache.specialTopic = [];
	data.tmp.fiscalYear = [];
	data.count.fiscalYear = {};
	cache.fiscalYear = [];

	data.searchResults = [];

	data.filters  = ['doc','cancerSite','researchType','specialTopic','fiscalYear'];
	data.selectedDoc = [];
	data.selectedCancerSite  = [];
	data.selectedResearchType = [];
	data.selectedSpecialTopic = [];
	data.selectedYear = [];


	categories.forEach(function(cat){
			var values = [];
			var tmpl = infos_category[cat.name].template_table;
			var filters = infos_category[cat.name].filters;

			gr = new GlideRecord(tmpl);

			gr.query();

			while (gr.next()) {

				var articleResult = {};
				articleResult.sys_id = gr.sys_id.toString();
				//articleResult.short_description = gr.short_description.toString();
				//articleResult.kb_knowledge_base = gr.getDisplayValue('kb_knowledge_base');
				articleResult.kb_category = gr.getDisplayValue('kb_category');
				articleResult.author = gr.getDisplayValue('author');
				//articleResult.sys_updated_on = gr.sys_updated_on.toString();
				//articleResult.published = gr.published.toString();
				//articleResult.sys_view_count = gr.sys_view_count.toString();
				articleResult.title = gr.x_26385_crs_kd_u_kb_title.toString();
				if(filters["cancerSite"]){
					articleResult.x_26385_crs_kd_u_kb_cancer_site_type = gr.x_26385_crs_kd_u_kb_cancer_site_type.toString();
				}
				if(filters["researchType"]){
					articleResult.x_26385_crs_kd_u_kb_research_type = gr.x_26385_crs_kd_u_kb_research_type.toString();
				}
				if(filters["doc"]){
					articleResult.x_26385_crs_kd_u_kb_doc = gr.x_26385_crs_kd_u_kb_doc.toString();
				}
				if(filters["specialTopic"]){
					articleResult.x_26385_crs_kd_u_kb_special_topic = gr.x_26385_crs_kd_u_kb_special_topic.toString();
				}
				if(filters["fiscalYear"]){
					articleResult.x_26385_crs_kd_u_kb_fiscal_year = gr.x_26385_crs_kd_u_kb_fiscal_year.toString();
					articleResult.fYear = articleResult.x_26385_crs_kd_u_kb_fiscal_year;
				}
				//articleResult.url = 'kb_view.do?sys_kb_id=' + articleResult.sys_id;
				articleResult.template = tmpl;
				values.push(articleResult);
				for(k in filters){
					var v = articleResult[filters[k]];
					if(v != ''){
						if(k == 'researchType' || k == 'doc'){
							v.split(",").forEach(function(elem){
								var idx = elem.indexOf('-');
								if(idx == -1){
									if(cache[k].indexOf(elem) == -1){
										cache[k].push(elem);
										data.tmp[k].push(elem);
										data.count[k][elem] = 1;
									}
									else{
										data.count[k][elem] ++;
									}
								}
								else{
									var em = elem.substring(0,idx) + '}';
									if(cache[k].indexOf(em) == -1){
										cache[k].push(em);
										data.tmp[k].push(em);
										data.count[k][em] = 1;
									}
									else{
										data.count[k][em] ++;
									}
								}

							});
						}
						else{
							v.split(",").forEach(function(elem){
								if(cache[k].indexOf(elem) == -1){
									cache[k].push(elem);
									data.tmp[k].push(elem);
									data.count[k][elem] = 1;
								}
								else{
									data.count[k][elem] ++;
								}
							});
						}

					}


				}
			}
			if(values.length > 0){
				var tab = {};
				tab.name = cat.name;
				tab.list = values;
				tab.show = true;
				data.searchResults.push(tab);
			}
		});
})();
