sp_widget.client_script:
function($scope, $location, $http,NERDSearchBasicArticleByCriteria) {
	/* widget controller */
	$scope.$emit('crs.header.display', false);
	var c = this;

	c.isExportable = true;
	c.selectedCount = 0;

	c.data.tabs = c.data.searchResults;
	c.selectedCategories = {};
	var index = 0;
	c.resultLen = 0;
	c.data.tabs.forEach(function(t){
		t.isOpen = true;
		c.selectedCategories[t.name] = index++;
		c.resultLen += t.list.length;
	});
	document.getElementById("cb_selectAll").checked = false;
	//c.searchResults = r.data.searchResults;
	c.selectedCount = 0;
	c.isExportable = false;

	c.data.maxSize = 5;
	c.data.pageSize = 10;
	c.data.bigCurrentPage = 1;

	c.pageChanged = function() {

  };

	var s = $location.search();

	c.search = function() {

		var params = {
									searchString: c.data.searchString && c.data.searchString !== ""? c.data.searchString : "", 
									knowledgeBaseID: c.data.selectedKnowledgeBase.id,
								  selectedYears: c.data.selectedYear.length > 0 ? c.data.selectedYear.join(",") : "",
									selectedDocs: c.data.selectedDoc.length > 0 ? c.data.selectedDoc.join(",") : "",
									selectedCancerSites: c.data.selectedCancerSite.length > 0 ? c.data.selectedCancerSite.join(",") : "",
									selectedResearchTypes: c.data.selectedResearchType.length > 0 ? c.data.selectedResearchType.join(",") : ""
								 };

		NERDSearchBasicArticleByCriteria.getList(params,c.data.sessionId).then(function(res){
			c.data.tabs = res.data.result;
			c.selectedCategories = {};
			var idx = 0;
			c.resultLen = 0;
			c.data.tabs.forEach(function(t){
				  t.isOpen = true;
					c.selectedCategories[t.name] = idx++;
					c.resultLen += t.list.length;
			});
			document.getElementById("cb_selectAll").checked = false;
			//c.searchResults = r.data.searchResults;
			c.selectedCount = 0;
			c.isExportable = false;

			c.data.maxSize = 5;
			c.data.pageSize = 10;
			c.data.bigCurrentPage = 1;
			$scope.$apply();
		}, function(error){
			console.log(error);
		});
	};

	c.showContent = function(template_id,doc_id){
		//$('#document_content').modal('show');
		window.displayData(doc_id,template_id);
	}

	c.updateFilter = function(cat){
		var idx = c.selectedCategories[cat.name];
		c.resultLen += cat.selected?c.data.tabs[idx].list.length: -c.data.tabs[idx].list.length;
		if(c.resultLen == 0){
			document.getElementById("cb_selectAll").checked = false;
			c.isExportable = false;
		}
		c.data.tabs[idx].show = !c.data.tabs[idx].show;
	}

	c.reset = function() {
		c.data.searchString = "";
		//c.data.selectedKnowledgeBase = {id: '-1', name: 'All'};
		c.data.selectedYear = [];
		c.data.selectedDoc = [];
	  c.data.selectedCancerSite = [];
		c.data.selectedResearchType = [];

		c.search();
	};

	//Search on enter key pressed
	var searchBox = document.getElementById("kbSearchInput");
		searchBox.addEventListener("keyup", function(event) {
		// Cancel the default action, if needed
		event.preventDefault();
		// Number 13 is the "Enter" key on the keyboard
		if (event.keyCode === 13) {
			// Trigger the button element with a click
			c.search();
		}
	});

	var selectAll = document.getElementById("cb_selectAll");

	selectAll.addEventListener('click', function() {
				var elements = document.getElementsByName("selections");
				if(this.checked) {
					for (var i = 0; i < elements.length; i++) {
						elements[i].checked = true;
					}
					c.isExportable = true;
				} else {
					for (var j = 0; j < elements.length; j++) {
						elements[j].checked = false;
					}
					c.isExportable = false;
				}
			}, false); 

	c.change = function(value){
		if(value){
			c.isExportable = true;
			c.selectedCount++;
		}
		else{
			c.selectedCount--;
			if(c.selectedCount <= 0){
				document.getElementById("cb_selectAll").checked = false;
				c.isExportable = false;
			}
		}
		console.log(c.selectedCount);
	}

	c.updateKnowledgeBaseData = function(item){
		if(item.selected){
			c.data.selectedKnowledgeBase.push(item.id);
		}
		else{
			var idx = c.data.selectedKnowledgeBase.indexOf(item.id);
			if (idx > -1) {
				c.data.selectedKnowledgeBase.splice(idx, 1);
			}
		}
		c.search();
	}

	c.updateCategoryData = function(item){
		if(item.selected){
			c.data.selectedCategory.push(item.name);
		}
		else{
			var idx = c.data.selectedCategory.indexOf(item.name);
			if (idx > -1) {
				c.data.selectedCategory.splice(idx, 1);
			}
		}
		c.search(1);
	}

	c.updateCancerSiteData = function(item){
		if(item.selected){
			c.data.selectedCancerSite.push(item.name);
		}
		else{
			var idx = c.data.selectedCancerSite.indexOf(item.name);
			if (idx > -1) {
				c.data.selectedCancerSite.splice(idx, 1);
			}
		}
		c.search();
	}

	c.selectAll_CancerSite = function(){
		var selectAll = document.getElementById("selectAll_CancerSite");
		c.data.selectedCancerSite = [];
		if(selectAll.checked){
			var tmp = [];
			c.data.cancerSite.map(function(obj){
				tmp.push(obj.name);
			});
			c.data.selectedCancerSite = tmp;
		}
		c.search();
	}

	c.updateResearchTypeData = function(item){
		if(item.selected){
			c.data.selectedResearchType.push(item.name);
		}
		else{
			var idx = c.data.selectedResearchType.indexOf(item.name);
			if (idx > -1) {
				c.data.selectedResearchType.splice(idx, 1);
			}
		}
		c.search();
	}

	c.selectAll_ResearchType = function(){
		var selectAll = document.getElementById("selectAll_ResearchType");
		c.data.selectedResearchType = [];
		if(selectAll.checked){
			var tmp = [];
			c.data.researchType.map(function(obj){
				tmp.push(obj.name);
			});
			c.data.selectedResearchType = tmp;
		}
		c.search();
	}

	c.updateDocData = function(item){
		if(item.selected){
			c.data.selectedDoc.push(item.name);
		}
		else{
			var idx = c.data.selectedDoc.indexOf(item.name);
			if (idx > -1) {
				c.data.selectedDoc.splice(idx, 1);
			}
		}
		c.search();
	}

	c.selectAll_Doc = function(){
		var selectAll = document.getElementById("selectAll_Doc");
		c.data.selectedDoc = [];
		if(selectAll.checked){
			var tmp = [];
			c.data.doc.map(function(obj){
				tmp.push(obj.name);
			});
			c.data.selectedDoc = tmp;
		}
		c.search();
	}

	c.updateYearData = function(item){
		if(item.selected){
			c.data.selectedYear.push(item.name);
		}
		else{
			var idx = c.data.selectedYear.indexOf(item.name);
			if (idx > -1) {
				c.data.selectedYear.splice(idx, 1);
			}
		}
		c.search();
	}

	c.selectAll_Year = function(){
		var selectAll = document.getElementById("selectAll_Year");
		c.data.selectedYear = [];
		if(selectAll.checked){
			var tmp = [];
			c.data.fiscalYear.map(function(obj){
				tmp.push(obj.name);
			});
			c.data.selectedYear = tmp;
		}
		c.search();
	}



	var htmlToWord = (function() {
      var uri = 'data:text/html,';
      var template = '<html xmlns:office="urn:schemas-microsoft-com:office:office" xmlns:word="urn:schemas-microsoft-com:office:word" xmlns="http://www.w3.org/TR/REC-html40"><head><xml><word:WordDocument><word:View>Print</word:View><word:Zoom>90</word:Zoom><word:DoNotOptimizeForBrowser/></word:WordDocument></xml></head><body>{content}</body></html>';
      return function(data) {
				 var a = document.createElement('a');
					document.body.appendChild(a);
					a.setAttribute('style', 'display:none');
					a.setAttribute('href', uri + encodeURIComponent((template.replace("{content}",data))));
					var today = new Date();
					var dd = today.getDate();
					var mm = today.getMonth()+1;
					var yyyy = today.getFullYear();

					if(dd<10) {
							dd = '0'+dd
					} 

					if(mm<10) {
							mm = '0'+mm
					} 

					today = yyyy + "-"+ mm + "-" + dd;	
					a.setAttribute('download', "NERD_report_"+today+".doc");
					a.click();
          document.body.removeChild(a);
      }
    })()

  c.export2Excel = function () {
		var cache = [];
		document.getElementsByName('selections').forEach(function(obj){
			if(obj.checked){
				var info = obj.value.split(',');
				cache.push(info[0]);
			}
		});
		var data = [];
		c.data.tabs.forEach(function(t){
			if(t.show){
				t.list.forEach(function(rs){
					if(cache.indexOf(rs.sys_id) >= 0){
						var tmp = {};
						tmp["Date entered"] = rs.published;
						tmp.Database = rs.kb_knowledge_base;
						tmp.Category = rs.kb_category;
						tmp.Title = rs.short_description;
						if(c.data.filters.includes('research_type')){
							tmp["Research Type"] = rs.x_26385_crs_kd_u_kb_research_type;
						}
						if(c.data.filters.includes('cancer_site')){
							tmp["Cancer Site/Type"] = rs.x_26385_crs_kd_u_kb_cancer_site_type;
						}
						if(c.data.filters.includes('doc')){
							tmp.Doc = rs.x_26385_crs_kd_u_kb_doc;
						}
						data.push(tmp);
					}
				});
			}

		});
		/* generate a worksheet */
		var ws = XLSX.utils.json_to_sheet(data);

		/* add to workbook */
		var wb = XLSX.utils.book_new();
		XLSX.utils.book_append_sheet(wb, ws, "sheet1");

		/* write workbook and force a download */
		var today = new Date();
		var dd = today.getDate();
		var mm = today.getMonth()+1;
		var yyyy = today.getFullYear();

		if(dd<10) {
				dd = '0'+dd
		} 

		if(mm<10) {
				mm = '0'+mm
		} 

		today = yyyy + "-"+ mm + "-" + dd;
		XLSX.writeFile(wb, "NERD_report_"+today+".xlsx");
  }

	c.export2Word = function () {
		var req = {
		 method: 'POST',
		 url: '/api/x_26385_crs_kd/export/word',
		 headers: {
			 'Content-Type': 'application/json'
		 }
		}

		req.data = {};
		req.data.files = [];
		document.getElementsByName('selections').forEach(function(obj){
			if(obj.checked){
				var fs = {};
				var info = obj.value.split(',');
				fs.sys_id = info[0];
				fs.kb = info[1];
				fs.category = info[2];
				req.data.files.push(fs);
			}
		});

		$http(req).then(function(resp){
			var content = resp.data.result.content;
			var dt = '';
			content.forEach(function(ct){
					dt += '<h1 style="text-align:center;">'+ct.title+'</h1>';
				  for(var key in ct){
						if(key != 'title'){
							dt += '<h3>'+key+':</h3>';
							dt += '<p>'+ct[key]+'</p>';
						}
					}
			});
			htmlToWord(dt);
		}, function(error_resp){
			console.log(error_resp);
		});

  }

}

sp_widget.script:
(function() {
	data.sessionId = gs.getSession().getSessionToken();
	var infos_category = {'Top Accomplishments': {template_table: 'u_kb_template_top_accomplishment', filters:{"cancerSite":"x_26385_crs_kd_u_kb_cancer_site_type",
																																																						 "researchType":"x_26385_crs_kd_u_kb_research_type",
																																																						 "doc":"x_26385_crs_kd_u_kb_doc",
																																																						 "fiscalYear":"x_26385_crs_kd_u_kb_fiscal_year"}}
							};
	data.knowledgeBases = [];
	var gr = new GlideRecord('kb_knowledge_base');
	gr.addQuery("title", "Basic Knowledge Base");
	gr.query();
	gr.next();
	var kb_id = gr.sys_id.toString();
	data.knowledgeBases.push({id: kb_id, name: gr.title.toString()});
	data.selectedKnowledgeBase = {id: kb_id, name: gr.title.toString()};

	gr = new GlideRecord('kb_category');
	gr.addQuery('parent_id', kb_id);
	gr.query();
	var categories = [];
	while (gr.next()) {
		var obj = {};
		categories.push({id: gr.sys_id.toString(), name: gr.label.toString()});
	}

	cache = {};
	data.doc = [];
	cache.doc = [];
	data.cancerSite = [];
	cache.cancerSite = [];
	data.researchType = [];
	cache.researchType = [];
	data.fiscalYear = [];
	cache.fiscalYear = [];

	data.searchResults = [];

	data.filters  = ['doc','cancerSite','researchType','fiscalYear'];
	data.selectedDoc = [];
	data.selectedCancerSite  = [];
	data.selectedResearchType = [];
	data.selectedYear = [];


	categories.forEach(function(cat){
			var values = [];
			var tmpl = infos_category[cat.name].template_table;
			var filters = infos_category[cat.name].filters;

			gr = new GlideRecord(tmpl);

			gr.query();

			while (gr.next()) {

				var articleResult = {};
				articleResult.sys_id = gr.sys_id.toString();
				articleResult.short_description = gr.short_description.toString();
				articleResult.kb_knowledge_base = gr.getDisplayValue('kb_knowledge_base');
				articleResult.kb_category = gr.getDisplayValue('kb_category');
				articleResult.author = gr.getDisplayValue('author');
				articleResult.sys_updated_on = gr.sys_updated_on.toString();
				articleResult.published = gr.published.toString();
				articleResult.sys_view_count = gr.sys_view_count.toString();
				articleResult.x_26385_crs_kd_u_kb_cancer_site_type = gr.x_26385_crs_kd_u_kb_cancer_site_type.toString();
				articleResult.x_26385_crs_kd_u_kb_research_type = gr.x_26385_crs_kd_u_kb_research_type.toString();
				articleResult.x_26385_crs_kd_u_kb_doc = gr.x_26385_crs_kd_u_kb_doc.toString();
				articleResult.x_26385_crs_kd_u_kb_fiscal_year = gr.x_26385_crs_kd_u_kb_fiscal_year.toString();
				articleResult.url = 'kb_view.do?sys_kb_id=' + articleResult.sys_id;
				articleResult.fYear = articleResult.x_26385_crs_kd_u_kb_fiscal_year;
				articleResult.template = tmpl;
				values.push(articleResult);
				for(k in filters){
					var v = articleResult[filters[k]];
					if(v.trim() != ''){
						v.trim().split(",").forEach(function(elem){
							if(cache[k].indexOf(elem) == -1){
								cache[k].push(elem);
								data[k].push({id: elem, name: elem});
							}
						});
					}

				}
			}
			if(values.length > 0){
				var tab = {};
				tab.name = cat.name;
				tab.list = values;
				tab.show = true;
				data.searchResults.push(tab);
			}
		});
})();
