	c.updateSpecialTopicData = function(item){
		if(item.selected){
			c.data.selectedSpecialTopic.push(item.id);
		}
		else{
			var idx = c.data.selectedSpecialTopic.indexOf(item.id);
			if (idx > -1) {
				c.data.selectedSpecialTopic.splice(idx, 1);
			}
		}
		c.search();
	}

