sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var number = request.body.data.number;
	var knowledge_base_id = request.body.data.kbId;
	var template_id = request.body.data.tmpId;
	var tag = request.body.data.tag;
	tag = "{" + tag + "}";

	var userid = gs.getUserID();

	var t = new GlideRecord('x_26385_crs_kd_tags');
	t.addQuery('article_number', number);
	t.addQuery('knowledge_base', knowledge_base_id);
	t.addQuery('userid', userid);
	t.query();
	var result = [];
	if(t.getRowCount() > 0){
		t.next();
		var tmp = t.tags.toString();
		var tags = tmp == "" ? [] : tmp.split("#");
		var idx = tags.indexOf(tag);
		if(idx == -1){
			tags.push(tag);
			t.tags = tags.join("#");
			t.update();
		}
		result = tags;
	}
	else{
		var a = new GlideRecord('x_26385_crs_kd_tags');
		a.initialize();
		a.article_number = number;
		a.knowledge_base = knowledge_base_id;
		a.userid = userid;
		a.tags = tag;
		a.insert();
		result = [];
		result.push(tag);
	}

	return result;

})(request, response);
