sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var compare = function(v_a, v_b){
		var pos_a = v_a.indexOf('.');
		var pos_b = v_b.indexOf('.');
		var v_a_l = parseInt(v_a.substring(0,pos_a));
		var v_b_l = parseInt(v_b.substring(0,pos_b));
		if(v_a_l < v_b_l){
			return 1;
		}
		else if(v_a_l == v_b_l){
			v_a_l = parseInt(v_a.substring(pos_a+1));
			v_b_l = parseInt(v_b.substring(pos_b+1));
			if(v_a_l < v_b_l){
				return 1;
			}
			else if(v_a_l == v_b_l){
				return 0;
			}
			else{
				return -1;
			}
		}
		else{
			return -1;
		}
	}
	var startVersion = request.queryParams.version.toString();
	var number = request.queryParams.number.toString();

    var v = new GlideRecord('x_26385_crs_kd_versions');
	v.addQuery('article_number', number);
	v.addQuery('article_field', 'annotation');
	/*
	if(startVersion && startVersion !== ""){
		v.addQuery('article_version', '<' ,startVersion);
	}
	v.orderByDesc('article_version');
	v.setLimit(5);
	*/
	v.query();
	var tmp = "";
	var uids = [];
	var dict = {};
	var vs = [];
	var versions = [];
	while(v.next()){
		var idx = v.record_version.toString();
		if(!startVersion || startVersion == "" || compare(startVersion,idx) < 0){
			tmp = v.sys_created_by.toString();
			dict[idx] = {version: idx,
					   annotation: v.article_field_value.toString(),
					   number: number,
					   editor: tmp,
					   edit_time: v.sys_created_on.toString().substring(0,10)
					  };
			vs.push(idx);
			if(uids.indexOf(tmp) == -1){
				uids.push(tmp);
			}
		}
	}

	//get User Name by User IDs
	var users = {};
	var user = new GlideRecord('sys_user');
	user.addQuery('user_name', 'IN', uids);
	user.query();
	 //var index = 0;
     while(user.next()){
		 var value = user.getDisplayValue('first_name') + " " + user.getDisplayValue('last_name');
		 users[user.getDisplayValue('user_name')] = value;
     }

	//get top 5 records
	vs.sort(compare);
	for(var i = 0; i< 5 & i < vs.length; i++){
		dict[vs[i]].editor = users[dict[vs[i]].editor];
		var date = dict[vs[i]].edit_time.replace(/-/g, '/');
		dict[vs[i]].edit_time = date.substring(5) + '/' + date.substring(0,4);
		versions.push(dict[vs[i]]);
	}

	return versions;

})(request, response);
