	var sub_id = sub.insert();

	if(data.skip_doc != undefined && data.skip_doc){
		var gr = new GlideRecord('x_26385_crs_kd_submissions');
		gr.addQuery('sys_id',sub_id);
		gr.query();
		gr.next();
		gr.submission_approval_action = 'approved';
		gr.update();
	}

