sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var data = request.body.data;
	var fileName = data.fileName;
	var fileContents = data.fileContents;
	var fileType = data.fileType;
	var template = data.template;
	var article_id = data.sys_id;

    var attachement = new GlideSysAttachment();
	var gr = new GlideRecord(template);
	var record = gr.get(article_id);
	var attachment_sys_id = attachement.writeBase64(gr, fileName , fileType, fileContents);
	var version = gr.x_26385_crs_kd_u_kb_record_version.toString();

	if(version != '0.1'){
		//if it's not during submission pharse, save attachment information to versions table
		var number = gr.number.toString();
		var v = new GlideRecord('x_26385_crs_kd_versions');
		v.addQuery("article_number", number);
		v.addQuery("record_version", version);
		v.addQuery("article_field", "attachments");
		v.query();
		if(v.getRowCount() == 0){
			var k = new GlideRecord('x_26385_crs_kd_versions');
			k.initialize(); 
			k.article_field = "attachments";
			k.record_version = version;
			k.article_number = number;
			k.article_field_value = attachment_sys_id;
			k.insert();
		}
		else{
			v.next();
			var attachments_str = v.article_field_value.toString();
			var attachments = attachments_str.split(",");
			attachments.push(attachment_sys_id);
			v.article_field_value = attachments.join(",");
			v.update();
		}
	}

	if(attachment_sys_id != null && attachment_sys_id.length > 0){
		return "1";
	}else{
		return "0";
	}

})(request, response);
