	var images = {};
	var img_fields = [];
	var submission = new GlideRecord(template);
	submission.initialize(); 
	submission.kb_knowledge_base = metadata[template].kb;
	submission.kb_category =  metadata[template].cat;
	metadata[template].schema.forEach(function(field){
		var str = data[field];
		if(str != undefined){
			var pos = str.indexOf('data:image/gif;base64,');
			if(pos > -1){
				img_fields.push(field);
				images[field] = [];
				var pos_end = 0;
				while(pos != -1){
					idx_start = pos;
					var part = str.slice(pos + 22);
					pos_end = part.indexOf('"');
					var b64 = part.slice(0, pos_end);
					var new_str = str.slice(0, pos) + "sys_attachment.do?sys_id=00000000000000000000000000000000" + part.slice(pos_end);
					images[field].push(b64);
					str = new_str;
					pos = str.indexOf('data:image/gif;base64,');
				}

			}
		}
		submission[field] = str;
		if(field == 'x_26385_crs_kd_u_kb_funding' && data[field].split('|')[0] == 'Yes'){
			submission.x_26385_crs_kd_u_kb_caution_warning = "No NCI Funding.";
		}
	});
	submission.author = gs.getUserID();
	submission.x_26385_crs_kd_u_kb_record_version = "1.0";
	var sys_id = submission.insert();

	//save inserted image if any
	if(img_fields.length > 0){
		var attach_ids = {};
		var gr = new GlideRecord(template);
		var record = gr.get(sys_id);
		img_fields.forEach(function(fld){
			attach_ids[fld] = [];
			images[fld].forEach(function(base64){
				var attachement = new GlideSysAttachment();
				var attachment_sys_id = attachement.writeBase64(gr, "img_" + Date.now() , "image/png", base64);
				attach_ids[fld].push(attachment_sys_id);
			});
		});
		var sbm = new GlideRecord(template);
		sbm.addQuery('sys_id', sys_id);
		sbm.query();
		sbm.next();
		for(var key in attach_ids){
			attach_ids[key].forEach(function(aid){
				sbm[key] = sbm[key].replace("00000000000000000000000000000000", aid);
			});
		}
		sbm.update();
	}

	//save annotation to versions table
	var a = new GlideRecord('x_26385_crs_kd_versions');
		a.initialize(); 
		a.article_field = "annotation";
		a.record_version = "1.0";
		a.article_number = submission.number.toString();
		a.article_field_value = "DOC Version";
		a.insert();

	return sys_id;

