	//var specifyVersion = params.specifyVersion; //targeting version
	var annotation = params.annotation; // targeting annotation
	var attachments = params.attachments.toString();
	var number = params.number;

	var result = {};
	var item = new GlideRecord(template);
	item.addQuery('number',number);
	item.query();
	item.next();
	var top_version = item.x_26385_crs_kd_u_kb_record_version.toString();
	var sys_id = item.sys_id.toString();
	if(isMainVersion){
		var pos = top_version.indexOf('.');
		var new_top_version = ""+ (parseInt(top_version.substring(0, pos)) + 1) + ".0";
		//replace the top version with this version
		var v = new GlideRecord('x_26385_crs_kd_versions');

		metadata[template].schema.forEach(function(field){
			v.initialize(); 
			v.article_field = field;
			v.record_version = top_version;
			v.article_number = number;
			v.article_field_value = item[field];
			v.insert();
		});
		//also save this version to main table for search purpose
