						var part = str.slice(pos + 22);
						pos_end = part.indexOf('"');
						var b64 = part.slice(0, pos_end);
						var attachement = new GlideSysAttachment();
						var attachment_sys_id = attachement.writeBase64(gr, "img_" + Date.now() , "image/png", b64);
						var new_str = str.slice(0, pos) + "sys_attachment.do?sys_id=" + attachment_sys_id + part.slice(pos_end);
						str = new_str;
						pos = str.indexOf('data:image/gif;base64,');
					}

				}
			}
			item[field] = str;
			if(field == 'x_26385_crs_kd_u_kb_funding' && params[field].split('|')[0] == 'Yes'){
				item.x_26385_crs_kd_u_kb_caution_warning = "No NCI Funding.";
			}
		});
		item.x_26385_crs_kd_u_kb_record_version = new_top_version;
		item.update();

		if(annotation == undefined || annotation == ""){
			annotation = "Main Version";
		}

		//annotation
		var a = new GlideRecord('x_26385_crs_kd_versions');
		a.initialize(); 
		a.article_field = "annotation";
		a.record_version = new_top_version;
		a.article_number = number;
		a.article_field_value = annotation;
		a.insert();
		result.version = new_top_version;
	}
	else{
		//incremental version based on the editing version
		var newVersion = "";
		var idx = version.indexOf('.');
		var newVersion_main  = "";
		if(idx == -1){
			newVersion_main = version;
		}
		else{
			newVersion_main = version.substring(0, idx);
		}
		var vn = new GlideRecord('x_26385_crs_kd_versions');
			vn.addQuery('record_version','STARTSWITH', newVersion_main + ".");
			vn.addQuery('article_number', number);
			vn.addQuery('article_field', 'annotation');
		    //vn.orderByDesc('article_version');
		    vn.query();
		    var max_sub_version = parseInt(version.substring(idx + 1));
		    while(vn.next()){
				var tmp = parseInt(vn.record_version.toString().substring(idx + 1));
				if(tmp > max_sub_version){
					max_sub_version = tmp;
				}
			}

			newVersion = newVersion_main + "." + (max_sub_version + 1) ;
			annotation = vn.article_field_value.toString();

		    var isTopVersion = true;
		    var pos_a = top_version.indexOf('.');
			var pos_b = idx;
			var v_a_l = parseInt(top_version.substring(0,pos_a));
			var v_b_l = parseInt(newVersion.substring(0,pos_b));
			if(v_a_l > v_b_l){
				isTopVersion = false;
			}
			else if(v_a_l == v_b_l){
				v_a_l = parseInt(top_version.substring(pos_a+1));
				v_b_l = parseInt(newVersion.substring(pos_b+1));
				if(v_a_l > v_b_l){
					isTopVersion = false;
				}
				else{
					isTopVersion = true;
				}
			}
			else{
				isTopVersion = true;
			}

		if(isTopVersion){
			//if top version is less than this version, replace the top version with this version
			var vk = new GlideRecord('x_26385_crs_kd_versions');

			metadata[template].schema.forEach(function(field){
				vk.initialize(); 
				vk.article_field = field;
				vk.record_version = top_version;
				vk.article_number = number;
				vk.article_field_value = item[field];
				vk.insert();
			});
			//also save this version to main table for search purpose
			metadata[template].schema.forEach(function(field){
				var str = params[field];
				if(str != undefined){
					var pos = str.indexOf('data:image/gif;base64,');
					if(pos > -1){
						var pos_end = 0;
						var gr = new GlideRecord(template);
						var record = gr.get(sys_id);
						while(pos != -1){
							idx_start = pos;
							var part = str.slice(pos + 22);
							pos_end = part.indexOf('"');
							var b64 = part.slice(0, pos_end);
							var attachement = new GlideSysAttachment();
							var attachment_sys_id = attachement.writeBase64(gr, "img_" + Date.now() , "image/png", b64);
							var new_str = str.slice(0, pos) + "sys_attachment.do?sys_id=" + attachment_sys_id + part.slice(pos_end);
							str = new_str;
							pos = str.indexOf('data:image/gif;base64,');
						}

					}
				}
				item[field] = str;

				if(field == 'x_26385_crs_kd_u_kb_funding' && params[field].split('|')[0] == 'Yes'){
					item.x_26385_crs_kd_u_kb_caution_warning = "No NCI Funding.";
				}
			});
			item.x_26385_crs_kd_u_kb_record_version = newVersion;
			item.update();
		}
		else{
			//else, only save this version to versions table
			var vm = new GlideRecord('x_26385_crs_kd_versions');

			metadata[template].schema.forEach(function(field){







				vm.initialize(); 
				vm.article_field = field;
				vm.record_version = newVersion;
				vm.article_number = number;

				var str = params[field];
				if(str != undefined){
					var pos = str.indexOf('data:image/gif;base64,');
					if(pos > -1){
						var pos_end = 0;
						var gr = new GlideRecord(template);
						var record = gr.get(sys_id);
						while(pos != -1){
							idx_start = pos;
							var part = str.slice(pos + 22);
							pos_end = part.indexOf('"');
							var b64 = part.slice(0, pos_end);
							var attachement = new GlideSysAttachment();
							var attachment_sys_id = attachement.writeBase64(gr, "img_" + Date.now() , "image/png", b64);
							var new_str = str.slice(0, pos) + "sys_attachment.do?sys_id=" + attachment_sys_id + part.slice(pos_end);
							str = new_str;
							pos = str.indexOf('data:image/gif;base64,');
						}

					}
				}
				vm.article_field_value = str;
				vm.insert();
			});
		}

		//annotation
		var a = new GlideRecord('x_26385_crs_kd_versions');
		a.initialize(); 
		a.article_field = "annotation";
		a.record_version = newVersion;
		a.article_number = number;
		a.article_field_value = annotation;
		a.insert();

		result.version = newVersion;
	}

	// if this version contains attachment from the editing version, save attachment sys_id
	if(attachments != ""){
		var att = new GlideRecord('x_26385_crs_kd_versions');
		att.initialize(); 
		att.article_field = "attachments";
		att.record_version = result.version;
		att.article_number = number;
		att.article_field_value = attachments;
		att.insert();
	}


	result.sys_id = sys_id;

	return result;
})(request, response);
