sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var number = request.queryParams.number.toString();

    var l = new GlideRecord('x_26385_crs_kd_linked_records');
	var tmp = l.addQuery('article_number', number);
	tmp.addOrCondition('reference_article', number);
	l.query();
	var links = [];
	var cat = {};
	while(l.next()){
		var an = l.article_number.toString();
		var ra = l.reference_article.toString();
		var tem = l.category.toString();
		var ref_tem = l.reference_category.toString();
		if(!(tem in cat)){
			cat[tem] = [];
		}
		if(!(ref_tem in cat)){
			cat[ref_tem] = [];
		}
		if(an == number){
			cat[ref_tem].push(ra);
		}
		else{
			cat[tem].push(an);
		}
	}

	for(var key in cat){
		var article = new GlideRecord(key);
		article.addQuery('number','IN', cat[key]);
		article.query();

		while(article.next()){
			var t = article.number.toString();
			links.push({
				aid: article.sys_id.toString(),
				kb_knowledge_base: article.kb_knowledge_base.toString(),
				number: t,
				tid: article.sys_class_name.toString(),
				title: article.x_26385_crs_kd_u_kb_title.toString()
			});
		}
	}
	return links;

})(request, response);
