	var params = request.body.data.params;
	var attachments = params.attachments.toString();
	var sub = new GlideRecord("x_26385_crs_kd_submissions");
	sub.addQuery("sys_id",params.sub_id);
	sub.query();
	sub.next();
	sub.submission_priority = params["submission_priority"];
	sub.submission_title = params["x_26385_crs_kd_u_kb_title"];
	/*
	var other_info = {};
	if(params.doc_contact_info){
