		other_info.crs_contact_info = params.crs_contact_info;
	}
	sub.submission_other_info = JSON.stringify(other_info);
	*/
	sub.update();
	var number = sub.submission_number.toString();
	var template = templates[sub.getDisplayValue("submission_knowledge_base")][sub.getDisplayValue("submission_category")];
	var item = new GlideRecord(template);
	item.addQuery("number",number);
	item.query();
	item.next();
	metadata[template].forEach(function(field){
			var str = params[field];
			if(str != undefined){
				var pos = str.indexOf('data:image/gif;base64,');
				if(pos > -1){
					var pos_end = 0;
					var gr = new GlideRecord(template);
					var record = gr.get(sys_id);
					while(pos != -1){
						idx_start = pos;
						var part = str.slice(pos + 22);
						pos_end = part.indexOf('"');
						var b64 = part.slice(0, pos_end);
						var attachement = new GlideSysAttachment();
						var attachment_sys_id = attachement.writeBase64(gr, "img_" + Date.now() , "image/png", b64);
						var new_str = str.slice(0, pos) + "sys_attachment.do?sys_id=" + attachment_sys_id + part.slice(pos_end);
						str = new_str;
						pos = str.indexOf('data:image/gif;base64,');
					}

				}
			}
			item[field] = str;
			if(field == 'x_26385_crs_kd_u_kb_funding' && params[field].split('|')[0] == 'Yes'){
				item.x_26385_crs_kd_u_kb_caution_warning = "No NCI Funding.";
			}
		});
	item.update();

	//delete attachments if any
	var toDelete = [];
	var existing_att = attachments == "" ? [] : attachments.split(',');
	var att = new GlideRecord('sys_attachment');
	att.addQuery("table_sys_id", item.sys_id.toString());
	att.addQuery('content_type','STARTSWITH','application/');
	att.query();
	while (att.next()){
		if(existing_att.indexOf(att.sys_id.toString()) == -1){
			toDelete.push(att.sys_id.toString());
		}
	}
	if(toDelete.length > 0){
		gs.eventQueue('x_26385_crs_kd.submission_attachment_del',sub, toDelete.join(','), "");
	}

	return {sub_id: params.sub_id, sys_id: item.sys_id.toString()};

})(request, response);
