		var filter = {};

		filter.searchString = searchString.toString();
		filter.selectedProject = selectedProject == ""? [] : selectedProject.toString().split(",");
		filter.selectedTags = selectedTags == "" ? [] : selectedTags.toString().split(",");
		filter.selectedMyTags = selectedMyTags == "" ? [] : selectedMyTags.toString().split(",");

		//if need to filter my tags
		var ids = [];
		if(filter.selectedMyTags.length > 0){
			var mt = new GlideRecord('x_26385_crs_kd_tags');
			mt.addQuery('knowledge_base', knowledgeBaseID);
			mt.addQuery('userid', userid);
			if (filter.selectedMyTags.length > 0) {
				var myTagsQuery = mt.addQuery('tags', 'CONTAINS', filter.selectedMyTags[0]);
				for(var j = 1; j< filter.selectedMyTags.length ; j++){
					myTagsQuery.addOrCondition('tags', 'CONTAINS', filter.selectedMyTags[j]);
				}
			}

			mt.query();
			while(mt.next()){
				ids.push(mt.article_number.toString());
			}
		}

		categories.forEach(function(cat){
			var values = [];
			var tmpl = infos_category[cat.name];
			var schema = info_schema[cat.name];
			gr = new GlideRecord(tmpl);
			gr.orderByDesc('sys_created_on');
			if (filter.searchString !== "") {
				// Index search
				gr.addQuery('IR_AND_QUERY', filter.searchString);
			}

			if (filter.selectedProject.length > 0) {
				if(schema["project"]){
					var projectQuery = gr.addQuery('x_26385_crs_kd_u_kb_project', 'CONTAINS', filter.selectedProject[0]);
					for(var j = 1; j< filter.selectedProject.length ; j++){
