					tagsQuery.addOrCondition('x_26385_crs_kd_u_kb_tags', 'CONTAINS', filter.selectedTags[j]);
				}
			}

			if(ids.length > 0){
				gr.addQuery('number', 'IN', ids);
			}

			gr.query();

			while (gr.next()) {

				var articleResult = {};
				articleResult.sys_id = gr.sys_id.toString();
				articleResult.number = gr.number.toString();
				articleResult.kb_category = gr.getDisplayValue('kb_category');
				articleResult.author = gr.getDisplayValue('author');
				articleResult.title = gr.x_26385_crs_kd_u_kb_title.toString();
				if(schema["project"]){
					articleResult.x_26385_crs_kd_u_kb_project = gr.x_26385_crs_kd_u_kb_project.toString();
				}
				articleResult.url = 'kb_view.do?sys_kb_id=' + articleResult.sys_id;
				articleResult.template = infos_category[articleResult.kb_category];
				values.push(articleResult);
			}
			if(values.length > 0){
				var tab = {};
				tab.name = cat.name;
				tab.list = values;
				tab.tmpl = tmpl;
				tab.show = true;
				tab.schema = schema;
				result.searchResults.push(tab);
			}
		});
