					if(blacklist.length == 0 || blacklist.indexOf(ckey) == -1){
						var str = gr[s[ckey]].toString();
						if(str.charAt(0) == '{'){
							dt[gr.sys_id][ckey] = str.replace(/{/g, '').replace(/}/g, '');
						}
						else{
							dt[gr.sys_id][ckey] = str;
						}
					}
				}
				dt[gr.sys_id].title = gr.x_26385_crs_kd_u_kb_title.toString();
				dt[gr.sys_id].category = gr.getDisplayValue('kb_category');
				dt[gr.sys_id]["Author"] = gr.getDisplayValue('author');
			}

	}
