sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var data = request.body.data;
	var files = data.files;
	var schemas = {"u_kb_template_triennial_report":{
					"Background":"u_kb_background",
					"Methods":"u_kb_methods",
					"Results":"u_kb_results",
					"Conclusions":"u_kb_conclusions"
				},"u_kb_template_top_accomplishment":{
					"Cancer Site/Type": "u_kb_cancer_site_type",
					  "Involved organization": "u_kb_involved_organization",
					  "References":"u_kb_references",
					  "Description":"u_kb_description",
					  "DOC":"u_kb_doc",
					  "Research Type":"u_kb_research_type"
				},"u_kb_template_significant_item":{
					"Cancer Site/Type": "u_kb_cancer_site_type",
					  "New Opportunities":"u_kb_new_opportunities",
					  "Recent Accomplishments":"u_kb_recent_accomplishments",
					  "Background":"u_kb_background",
					  "Congressional Language":"u_kb_template_significant_item"
				},"u_kb_template_collaboration_report":{
					"Description":"u_kb_description",
					    "Federal Partners":"u_kb_federal_partners",
					    "NIH Partners":"u_kb_nih_partners",
					    "Type":"u_kb_type"
				}};
	var config = {"Final Report Knowledge Base>Triennial Report":"u_kb_template_triennial_report",
				  "Basic Knowledge Base>Top Accomplishments":"u_kb_template_top_accomplishment",
				  "Basic Knowledge Base>Significant Items":"u_kb_template_significant_item",
				  "Basic Knowledge Base>Collaboration Report":"u_kb_template_collaboration_report"
	};
	var body = {};
	body.content = [];
	var cache = {};
	files.forEach(function(f){
		var tmp = config[f.kb + ">" +f.category];
		if(cache[tmp] == null){
			cache[tmp] = [];
		}
		cache[tmp].push(f.sys_id);
	});
	var dt = {};
	var gr;
	for(var key in cache){
		var s = schemas[key];
		gr = new GlideRecord(key);
		gr.addQuery("sys_id", "IN", cache[key]);
		gr.query();

		while (gr.next()) {
			dt[gr.sys_id] = {};
			for(var ckey in s){
				dt[gr.sys_id][ckey] = gr[s[ckey]];
			}
			dt[gr.sys_id].title = gr.short_description;
		}
	}
	files.forEach(function(fs){
		body.content.push(dt[fs.sys_id]);
	});

	response.setBody(body);	
})(request, response);
