sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var params = request.body.data.params;
    var sub_id = params.sub_id;
	var doc_comment = params.doc_comment;

	var gr = new GlideRecord('x_26385_crs_kd_submissions');
	gr.addQuery('sys_id',sub_id);
	gr.query();
	gr.next();
	gr.submission_approval_action = 'rejected';
	gr.submission_doc_reviewer = gs.getUserID();
	var other_info = JSON.parse(gr.submission_other_info.toString());
	other_info.rejectedByDOC = 1;
	gr.submission_other_info = JSON.stringify(other_info);
	gr.doc_comment = gr.doc_comment == "" ? doc_comment : gr.doc_comment + ";;" + doc_comment;
	gr.update();

	//email notification to NCI staff member. todo...
	var email = gr.submission_author.email;
	gs.eventQueue('x_26385_crs_kd.doc_reject_notify',gr, doc_comment, email);

	return 1;

})(request, response);
